    @Override
    public boolean executeStep() {
        this.setActive(false);

        try {
            int players = Integer.parseInt(this.playerCount.getText());
            Game.getInstance().createPlayers(players);
            return false;
        } catch (Exception e) {
            JOptionPane.showMessageDialog(this, "Ingrese un valor num√©rico", "Error",
                    JOptionPane.ERROR_MESSAGE);
            return true;
        }
    }
